import React, { useEffect } from 'react';
import './about.css';
import Navbar from '../../components/navbar/navbar';
import Footer from '../../components/footer/footer';
import Background from '../../components/background/Background';
import Loading from '../../Assets/loading.gif';

function Cart() {
  // Add animation class when elements enter the viewport
  useEffect(() => {
    // Optional: Add intersection observer for scroll animations
    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          entry.target.classList.add('visible');
        }
      });
    }, { threshold: 0.1 });

    document.querySelectorAll('.cart-item').forEach(item => {
      observer.observe(item);
    });

    return () => {
      document.querySelectorAll('.cart-item').forEach(item => {
        observer.unobserve(item);
      });
    };
  }, []);

  return (
    <div className="page-wrapper">
      {/* Add animated background div */}
      <div className="background-animation"></div>
      
      <Navbar />
      <Background />
      
      <div className="cart-container">
        <h1 className="welcome-text">Welcome to AtomKey Lab ðŸ”¬</h1>
        <p className="quote-about">"Discover the Wonders of Science with Every Experiment"</p>
        <img
          src={Loading}
          alt="Virtual Lab"
          className="lab-image-head"
        />
      </div>

      <div className="cart-items">
        <div className="cart-item">
          <div className="image-container">
            <img
              src="https://img.freepik.com/premium-photo/virtual-reality-chemistry-labs-immersive-learning-futuristic-classrooms_839035-21538.jpg"
              alt="Immersive Interactive"
              className="lab-image"
            />
          </div>
          <h3 className='cart-item-h3'>Immersive Interactive</h3>
          <p className="cart-item-desc">Experience chemistry through engaging virtual simulations and interactive 3D models</p>
        </div>
        
        <div className="cart-item">
          <div className="image-container">
            <img
              src="https://thumbs.dreamstime.com/b/hour-clock-hand-nonstop-client-service-concept-businessman-show-virtual-worldwide-nonstop-support-help-full-time-available-299145163.jpg"
              alt="24/7 Availability"
              className="lab-image"
            />
          </div>
          <h3 className='cart-item-h3'>24/7 Availability</h3>
          <p className="cart-item-desc">Access lab resources and experiments at any time that suits your schedule</p>
        </div>
        
        <div className="cart-item">
          <div className="image-container">
            <img
              src="https://webfiles.amrita.edu/2020/12/xI0ie3Ap-chemistryvl.jpg"
              alt="Cost Effective"
              className="lab-image"
            />
          </div>
          <h3 className='cart-item-h3'>Cost Effective</h3>
          <p className="cart-item-desc">Save on expensive lab equipment and materials with virtual alternatives</p>
        </div>
        
        <div className="cart-item">
          <div className="image-container">
            <img
              src="data:image/jpeg;base64,"
              alt="Experiment Without Constraints"
              className="lab-image"
            />
          </div>
          <h3 className='cart-item-h3'>Experiment Without Constraints</h3>
          <p className="cart-item-desc">Unlimited resources for all your scientific explorations and experiments</p>
        </div>
        
        <div className="cart-item feature-safety">
        <img 
            src="data:image/png;base64," 
            alt="Item 5" 
            className="lab-image" 
          />
          <h3 className='cart-item-h3'>Safety</h3>
          {/* Note: The description for items 5 and 6 is handled via CSS ::after pseudo-element */}
          <p className="cart-item-desc">Perform experiments safely without physical risks or hazards</p>
        </div>
        
        <div className="cart-item feature-anywhere">
        <img 
            src="data:image/jpeg;base64," 
            alt="Item 6" 
            className="lab-image" 
          />
          <h3 className='cart-item-h3'>AnyTime, Anywhere</h3>
          {/* Note: The description for items 5 and 6 is handled via CSS ::after pseudo-element */}
          
          <p className="cart-item-desc">Access your virtual lab from any device with internet connectivity</p>
        </div>
      </div>
      
      {/* Call-to-action button */}
      
      
      <Footer />
    </div>
  );
}

export default Cart;